const products = [
  {
    id: 1,
    name: "Fren BalatasÄ± - Yamaha R25",
    price: 320,
    image: "data:image/jpeg;base64,"
  },
  {
    id: 2,
    name: "Zincir Seti - Honda CBR",
    price: 740,
    image: "data:image/jpeg;base64,"
  },
  {
    id: 3,
    name: "Lastik - Michelin 120/70-17",
    price: 1500,
    image: "data:image/jpeg;base64,"
  }
];

const cart = {};
const cartItems = document.getElementById("cart-items");
const cartTotal = document.getElementById("cart-total");

function updateCart() {
  cartItems.innerHTML = "";
  let total = 0;

  for (let id in cart) {
    const item = cart[id];

    const li = document.createElement("li");
    li.textContent = `${item.name} x${item.quantity} - âº${item.price * item.quantity}`;

    const removeBtn = document.createElement("button");
    removeBtn.textContent = "Sil";
    removeBtn.style.marginLeft = "10px";
    removeBtn.style.background = "#dc2626";
    removeBtn.style.color = "#fff";
    removeBtn.style.border = "none";
    removeBtn.style.borderRadius = "4px";
    removeBtn.style.padding = "3px 8px";
    removeBtn.style.cursor = "pointer";

    removeBtn.addEventListener("click", () => {
      delete cart[id];
      updateCart();
    });

    li.appendChild(removeBtn);
    cartItems.appendChild(li);

    total += item.price * item.quantity;
  }

  cartTotal.textContent = total;
}

function addToCart(product) {
  if (cart[product.id]) {
    cart[product.id].quantity += 1;
  } else {
    cart[product.id] = { ...product, quantity: 1 };
  }
  updateCart();
}

const container = document.getElementById("product-list");

products.forEach(product => {
  const card = document.createElement("div");
  card.className = "card";
  card.innerHTML = `
    <img src="${product.image}" alt="${product.name}">
    <h2>${product.name}</h2>
    <p>âº${product.price}</p>
    <button class="btn">Sepete Ekle</button>
  `;
  const button = card.querySelector(".btn");
  button.addEventListener("click", () => addToCart(product));
  container.appendChild(card);
});
